// frontend/src/component/Header.js
import React from 'react';
import { NavLink } from 'react-router-dom';
import useAuth from '../hooks/useAuth';

const Header = () => {
  const { user, logout } = useAuth();

  const activeLinkStyle = {
    color: '#4f46e5',
    fontWeight: '600',
  };

  const isAdmin = user && (user.role === 'admin' || user.role === 'owner');
  const isOwner = user && user.role === 'owner';

  return (
    <header className="bg-white border-b border-gray-200 shadow-md sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          <div className="flex items-center space-x-2">
            <NavLink to="/dashboard" className="text-3xl font-extrabold text-indigo-700 tracking-tight">
              Contact Page Submitter
            </NavLink>
            <span className="text-sm font-medium text-gray-500 mt-1 hidden sm:inline-block">AI Personalized Messages!</span>
          </div>

          <nav className="hidden md:flex items-center space-x-6">
            <NavLink to="/dashboard" style={({ isActive }) => isActive ? activeLinkStyle : undefined} className="text-gray-600 hover:text-indigo-700 transition font-medium">Dashboard</NavLink>
            <NavLink to="/campaigns" style={({ isActive }) => isActive ? activeLinkStyle : undefined} className="text-gray-600 hover:text-indigo-700 transition font-medium">Campaigns</NavLink>
            <NavLink to="/settings" style={({ isActive }) => isActive ? activeLinkStyle : undefined} className="text-gray-600 hover:text-indigo-700 transition font-medium">Settings</NavLink>
            {isAdmin && (
              <NavLink to="/admin" style={({ isActive }) => isActive ? activeLinkStyle : undefined} className="text-gray-600 hover:text-indigo-700 transition font-medium">Admin</NavLink>
            )}
            {isOwner && (
              <NavLink to="/owner" style={({ isActive }) => isActive ? activeLinkStyle : undefined} className="text-gray-600 hover:text-indigo-700 transition font-medium">Owner</NavLink>
            )}
          </nav>

          <div className="flex items-center">
            <button
              onClick={logout}
              className="px-4 py-2 bg-indigo-600 text-white text-sm font-semibold rounded-md shadow hover:bg-indigo-700 transition"
            >
              Logout
            </button>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
