import React from 'react';
import { NavLink } from 'react-router-dom';
import useAuth from '../hooks/useAuth';

const Header = () => {
  const { user, logout } = useAuth();

  const activeLinkStyle = {
    color: '#4f46e5',
    fontWeight: '600',
  };

  const isAdmin = user && (user.role === 'admin' || user.role === 'owner');
  const isOwner = user && user.role === 'owner';

  return (
    <header className="bg-white shadow-sm">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          <div className="flex-shrink-0">
            <NavLink to="/dashboard" className="text-2xl font-bold text-indigo-600">CPS</NavLink>
          </div>
          <nav className="hidden md:flex items-center space-x-8">
            <NavLink to="/dashboard" style={({ isActive }) => isActive ? activeLinkStyle : undefined} className="text-gray-500 hover:text-gray-900">Dashboard</NavLink>
            <NavLink to="/campaigns" style={({ isActive }) => isActive ? activeLinkStyle : undefined} className="text-gray-500 hover:text-gray-900">Campaigns</NavLink>
            <NavLink to="/settings" style={({ isActive }) => isActive ? activeLinkStyle : undefined} className="text-gray-500 hover:text-gray-900">Settings</NavLink>
            {isAdmin && (
              <NavLink to="/admin" style={({ isActive }) => isActive ? activeLinkStyle : undefined} className="text-gray-500 hover:text-gray-900">Admin</NavLink>
            )}
            {isOwner && (
              <NavLink to="/owner" style={({ isActive }) => isActive ? activeLinkStyle : undefined} className="text-gray-500 hover:text-gray-900">Owner</NavLink>
            )}
          </nav>
          <div>
            <button
              onClick={logout}
              className="px-4 py-2 bg-indigo-100 text-indigo-700 text-sm font-medium rounded-md hover:bg-indigo-200"
            >
              Logout
            </button>
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
