// frontend/src/pages/AdminDashboard.jsx

import React, { useEffect, useState } from 'react';
import api from '../services/api';

const AdminDashboard = () => {
  const [summary, setSummary] = useState(null);

  useEffect(() => {
    const fetchAdminSummary = async () => {
      try {
        const res = await api.get('/admin/summary');
        setSummary(res.data);
      } catch (err) {
        console.error('Failed to load admin summary:', err);
      }
    };

    fetchAdminSummary();
  }, []);

  if (!summary) {
    return <div className="text-center py-12">Loading admin stats...</div>;
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
      <h1 className="text-2xl font-bold text-gray-900 mb-6">Admin Dashboard</h1>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-6">
        <div className="bg-white p-6 rounded shadow">
          <h2 className="text-lg font-medium text-gray-700">Total Users</h2>
          <p className="text-3xl font-bold text-indigo-600">{summary.total_users}</p>
        </div>
        <div className="bg-white p-6 rounded shadow">
          <h2 className="text-lg font-medium text-gray-700">Campaigns Created</h2>
          <p className="text-3xl font-bold text-indigo-600">{summary.total_campaigns}</p>
        </div>
        <div className="bg-white p-6 rounded shadow">
          <h2 className="text-lg font-medium text-gray-700">Total Submissions</h2>
          <p className="text-3xl font-bold text-indigo-600">{summary.total_submissions}</p>
        </div>
        <div className="bg-white p-6 rounded shadow">
          <h2 className="text-lg font-medium text-gray-700">Failed Captchas</h2>
          <p className="text-3xl font-bold text-red-500">{summary.failed_captchas}</p>
        </div>
      </div>

      {/* Optionally add charts, tables, or export buttons here */}
    </div>
  );
};

export default AdminDashboard;
