import React, { useState, useEffect } from 'react';
import apiClient from '../api/apiClient';
import useAuth from '../hooks/useAuth';

const SettingsPage = () => {
  const { user } = useAuth();
  const [formData, setFormData] = useState({
    first_name: '',
    last_name: '',
    company_name: '',
    title: '',
    phone_number: '',
    website: '',
    dbc_username: '',
    dbc_password: '',
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Fetch user data on component mount
  useEffect(() => {
    const fetchUserData = async () => {
      if (!user) return;
      try {
        setLoading(true);
        // This endpoint gets the full user object, including contact_info
        const response = await apiClient.get('/users/me'); 
        const userData = response.data;
        
        setFormData({
          first_name: userData.first_name || '',
          last_name: userData.last_name || '',
          company_name: userData.contact_info?.company_name || '',
          title: userData.contact_info?.title || '',
          phone_number: userData.contact_info?.phone_number || '',
          website: userData.contact_info?.website || '',
          dbc_username: userData.dbc_username || '',
          dbc_password: '', // Don't pre-fill password
        });
        setError('');
      } catch (err) {
        setError('Failed to load user data.');
        console.error(err);
      } finally {
        setLoading(false);
      }
    };

    fetchUserData();
  }, [user]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    try {
      // Separate the data for different API endpoints
      const userUpdateData = {
        first_name: formData.first_name,
        last_name: formData.last_name,
      };
      
      const contactInfoUpdateData = {
        company_name: formData.company_name,
        title: formData.title,
        phone_number: formData.phone_number,
        website: formData.website,
      };

      const settingsUpdateData = {
        dbc_username: formData.dbc_username,
        dbc_password: formData.dbc_password,
      };

      // In a real app, you'd likely have dedicated endpoints for these
      // For now, we assume a general /users/me endpoint can handle this
      await apiClient.put('/users/me', {
        ...userUpdateData,
        ...contactInfoUpdateData, // This assumes the backend can handle nested updates
        ...settingsUpdateData,
      });

      setSuccess('Settings saved successfully!');
    } catch (err) {
      setError('Failed to save settings. Please try again.');
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  if (loading && !formData.first_name) {
    return <div>Loading settings...</div>;
  }

  return (
    <div className="bg-white p-8 rounded-lg shadow-md">
      <h1 className="text-2xl font-bold text-gray-900 mb-6">Settings</h1>
      
      <form onSubmit={handleSubmit} className="space-y-6">
        {/* Contact Persona Section */}
        <div>
          <h2 className="text-lg font-semibold text-gray-800 border-b pb-2 mb-4">Contact Persona</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-sm font-medium text-gray-700">First Name</label>
              <input type="text" name="first_name" value={formData.first_name} onChange={handleInputChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm" />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Last Name</label>
              <input type="text" name="last_name" value={formData.last_name} onChange={handleInputChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm" />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Company Name</label>
              <input type="text" name="company_name" value={formData.company_name} onChange={handleInputChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm" />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">Title / Position</label>
              <input type="text" name="title" value={formData.title} onChange={handleInputChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm" />
            </div>
          </div>
        </div>

        {/* CAPTCHA Service Section */}
        <div>
          <h2 className="text-lg font-semibold text-gray-800 border-b pb-2 mb-4">CAPTCHA Service</h2>
           <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              <label className="block text-sm font-medium text-gray-700">DeathByCaptcha Username</label>
              <input type="text" name="dbc_username" value={formData.dbc_username} onChange={handleInputChange} className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm" />
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700">DeathByCaptcha Password</label>
              <input type="password" name="dbc_password" value={formData.dbc_password} onChange={handleInputChange} placeholder="Enter new password to change" className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm" />
            </div>
          </div>
        </div>

        {/* Submission and Feedback */}
        <div className="text-right">
          {error && <p className="text-red-500 text-sm text-left mb-2">{error}</p>}
          {success && <p className="text-green-500 text-sm text-left mb-2">{success}</p>}
          <button type="submit" disabled={loading} className="px-6 py-2 bg-indigo-600 text-white font-medium rounded-md hover:bg-indigo-700 disabled:bg-indigo-300">
            {loading ? 'Saving...' : 'Save Settings'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default SettingsPage;
